map = JSON.parse(map);

const width = 64;
const height = 64;
var subdivide = (can.width/500);
var mouseX,mouseY;
var navigationMap = Map();
var startPos;
var targetPos;
var navMapLoadState = -1;
var Path = [];
//navigationMap[startPos.x][startPos.y] = new navigationPoint(startPos.x,startPos.y);

//var map = Map();
function vector2(x,y) {
	this.x = x;
	this.y = y;
}

function Map(defeult = 0) {
	let array = new Array(width);
	for (var x = 0; x < width; x++) {
		array[x] = [0];
		for (var y = 0; y < height; y++) {
			array[x][y] = 0;
		}
	}
	return array;
}

function DrawMap(array) {
	ctx.clearRect(0,0,width,height);
	for (var x = 0; x < width; x++) {
		for (var y = 0; y < height; y++) {
			if (array[x][y]!=0) {
				ctx.fillStyle = "#ffffff";
				ctx.fillRect(x,y,1,1);
			}
			if (navigationMap[x][y]!=0) {
				ctx.fillStyle = "#898a6e";//da7f73
				ctx.fillRect(x,y,1,1);
			}
		}
	}
	for (var p = 0; p < Path.length; p++) {
		ctx.fillStyle = "#da7f73";
		ctx.fillRect(Path[p].x,Path[p].y,1,1);		
	}

	if (startPos!=undefined) {
		ctx.fillStyle = "#f7b668";
		ctx.fillRect(startPos.x,startPos.y,1,1);	
	}
	if (targetPos!=undefined) {
		ctx.fillStyle = "#7afee3";
		ctx.fillRect(targetPos.x,targetPos.y,1,1);
	}
}

function navigationPoint(x,y) {
	this.builded = false;
	this.stepNumber = 0;
	this.pos = new vector2(x,y);
	this.build = function() {
		if (!this.builded) {
			checkPointPosition(this.pos.x,this.pos.y-1,this.stepNumber);
			checkPointPosition(this.pos.x+1,this.pos.y,this.stepNumber);
			checkPointPosition(this.pos.x,this.pos.y+1,this.stepNumber);
			checkPointPosition(this.pos.x-1,this.pos.y,this.stepNumber);
			this.builded = true;
		}
	}
	this.findPath = function() {
		if (this.pos!=targetPos) {
			if (findPathAtPosition(this.pos.x,this.pos.y-1,this.stepNumber)) {addPath(this.pos.x,this.pos.y-1);}else
			if (findPathAtPosition(this.pos.x+1,this.pos.y,this.stepNumber)) {addPath(this.pos.x+1,this.pos.y);}else
			if (findPathAtPosition(this.pos.x,this.pos.y+1,this.stepNumber)) {addPath(this.pos.x,this.pos.y+1);}else
			if (findPathAtPosition(this.pos.x-1,this.pos.y,this.stepNumber)) {addPath(this.pos.x-1,this.pos.y);}
		}
	}
}

function checkPointPosition(x,y,step) {
	if (map[x][y]==0 && navigationMap[x][y]==0) {
		navigationMap[x][y] = new navigationPoint(x,y);
		navigationMap[x][y].stepNumber = step+1;
	}
}
function findPathAtPosition(x,y,step) {
	if (navigationMap[x][y]!=0) {
		if (navigationMap[x][y].stepNumber<step) {
			return true;
		}
	}
}
function addPath(x,y) {
	Path.push(navigationMap[x][y].pos);
	//navigationMap[x][y].findPath();
}

document.addEventListener('keydown', function(e) {
	if (String.fromCharCode(e.keyCode)=="E") {
		let x = Math.round(subdivide*mouseX-1.5);
		let y = Math.round(subdivide*mouseY-1.5);
		if (map[x][y]==1) {
			map[x][y] = 0;
		}
	}
	if (String.fromCharCode(e.keyCode)=="P") {
		let x = Math.round(subdivide*mouseX-1.5);
		let y = Math.round(subdivide*mouseY-1.5);
		console.log("x:"+x+"; y:"+y);
	}
});

can.addEventListener('mousedown', function(e) {
	mouseDown = true;
	var v = new vector2(Math.round(subdivide*e.pageX-1.5),Math.round(subdivide*e.pageY-1.5));
	if (startPos==undefined && map[v.x][v.y]==0) {
		startPos = v;
	}else if (targetPos==undefined && map[v.x][v.y]==0) {
		targetPos = v;
		navigationMap[startPos.x][startPos.y] = new navigationPoint(startPos.x,startPos.y);
		navMapLoadState = 0;
	}else{
	}
});

can.addEventListener('mouseup', function(e) {
	mouseDown = false;
});

can.addEventListener('mousemove', function(e) {
	mouseX = e.pageX;
	mouseY = e.pageY;
});




setInterval(function() {
	DrawMap(map);
	if(mouseDown) {
		// map[x][y] = 1;
	}
	let x = Math.round(subdivide*mouseX-1.5);
	let y = Math.round(subdivide*mouseY-1.5);
	ctx.fillStyle = "#bea688";
	ctx.fillRect(x,y,1,1);
	if (navMapLoadState == 0) {
		loop1: for (var npx = 0; npx < navigationMap.length; npx++) {
			loop2: for (var npy = 0; npy < navigationMap[0].length; npy++) {
				if (navigationMap[npx][npy].build!=undefined && navigationMap[targetPos.x][targetPos.y]==0) {
					navigationMap[npx][npy].build();
				}else if(navigationMap[targetPos.x][targetPos.y]!=0){
					navMapLoadState = 1;
					break loop2;
					break loop1;
				}
			}
		}
	}else if(navMapLoadState==1){
		navigationMap[targetPos.x][targetPos.y].findPath();
		navMapLoadState = 2;
	}else if (navMapLoadState==2) {
		navigationMap[Path[Path.length-1].x][Path[Path.length-1].y].findPath();
	}
},1000/15);












